static void bar_update_cb(lv_timer_t * timer)  
{  
    if(HF_ui_screen_id==3||HF_open==1)
    {
       
       
        
        // 每次调用时增加进度条的值（可根据需要调整增量）  
        int i=options2[selected_index];
        
        // bar_value += 5;
        if(i==0)
         i=15;
        bar_value =bar_value+(100/i);
    
        // 若值达到或超过100，则重置为0（对于对称模式，您可能希望设置为某个最大值而非100）  
        if(bar_value >= 100) {  
        // lv_timer_pause(bar_time);
        bar_value = 0;  
         HF_ui_screen_id=1;
        _ui_screen_change(&ui_Screen1, LV_SCR_LOAD_ANIM_FADE_ON, 5, 0, &ui_Screen1_screen_init);
        }  
        //current_value
        // 更新进度条显示  
        
        lv_bar_set_value(ui_Bar1, bar_value, LV_ANIM_ON); // 启用动画效果  
        

        
        char num_str[16]={0}; // 对于32位整数来说，10个字符足够（包括负号和空字符）  
        
        //暂时不用了，不能和进度条反向同步
        // 使用snprintf将整数转换为字符串  
        // HF_number=(bar_value*i)/100;
        // snprintf(num_str, sizeof(num_str), "%d", bar_value);  
        
       
        // _ui_label_set_property(ui_Label1, _UI_LABEL_PROPERTY_TEXT, num_str);
    }
    else
    {
      bar_value = 0;  
      lv_bar_set_value(ui_Bar1, 0, LV_ANIM_ON); // 启用动画效果    
    }
}  
